export const DiscordStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { Hash, SmilePlus } from 'lucide-react';\nimport { useState } from 'react';\nimport {\n  DropdownMenu,\n  DropdownMenuContent,\n  DropdownMenuItem,\n  DropdownMenuTrigger,\n} from '@/components/ui/dropdown-menu';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst REACTION_EMOJIS = [\n  { id: 'like', emoji: 'üëç' },\n  { id: 'dislike', emoji: 'üëé' },\n  { id: 'love', emoji: '‚ù§Ô∏è' },\n  { id: 'haha', emoji: 'üòÇ' },\n  { id: 'wow', emoji: 'üòÆ' },\n  { id: 'sad', emoji: 'üò¢' },\n  { id: 'angry', emoji: 'üò°' },\n];\n\nconst roleColors: Record<string, string> = {\n  'Sarah Chen': '#EB459E', 'Alex Rivera': '#57F287', 'Jordan Park': '#FEE75C',\n  'Maya Patel': '#ED4245', 'Chris Lee': '#5865F2',\n};\n\nconst DiscordMessage = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { isAuthor, edit, reply, reaction, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const nameColor = roleColors[comment.author.name] || '#FFFFFF';\n  const replies = comment.replies || [];\n\n  return (\n    <>\n      <div className=\"group flex gap-4 hover:bg-[#2E3035]/50 px-4 py-1.5 -mx-4 rounded-md transition-colors relative\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full mt-0.5 shrink-0\" />\n        <div className=\"flex-1 min-w-0\">\n          <div className=\"flex items-baseline gap-2\">\n            <span className=\"text-[15px] font-medium hover:underline cursor-pointer\" style={{ color: nameColor }}>{comment.author.name}</span>\n            <span className=\"text-[11px] text-[#949BA4]\">{formatRelativeTime(comment.createdAt)}</span>\n          </div>\n          {edit.isEditing ? (\n            <div className=\"mt-1\">\n              <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#383A40] rounded-lg p-2.5 text-sm text-[#DBDEE1] mt-1 outline-none border border-transparent focus:border-[#5865F2] transition-colors\" />\n              <div className=\"flex gap-2 mt-1 text-xs\">\n                <span className=\"text-[#949BA4]\">escape to</span>\n                <button onClick={edit.cancelEdit} className=\"text-[#00A8FC] hover:underline\">cancel</button>\n                <span className=\"text-[#949BA4]\">¬∑ enter to</span>\n                <button onClick={edit.submitEdit} className=\"text-[#00A8FC] hover:underline\">save</button>\n              </div>\n            </div>\n          ) : (\n            <p className=\"text-[15px] text-[#DBDEE1] leading-relaxed\">{comment.content}</p>\n          )}\n          <div className=\"flex flex-wrap gap-1.5 mt-1.5\">\n            {comment.reactions?.filter(r => r.count > 0).map(r => (\n              <button key={r.id} onClick={() => reaction.toggle(r.id)} className={`flex items-center gap-1.5 text-xs px-2 py-1 rounded-md transition-colors ${r.isActive ? 'bg-[#5865F2]/20 border border-[#5865F2]/50 text-[#DEE0FC]' : 'bg-[#2B2D31] border border-transparent hover:border-[#4E5058] text-[#B5BAC1]'}`}>\n                {r.emoji} <span>{r.count}</span>\n              </button>\n            ))}\n            <DropdownMenu>\n              <DropdownMenuTrigger asChild>\n                <button className=\"flex items-center justify-center w-7 h-7 rounded-md bg-[#2B2D31] hover:border-[#4E5058] border border-transparent text-[#B5BAC1] transition-colors opacity-0 group-hover:opacity-100\">\n                  <SmilePlus className=\"w-3.5 h-3.5\" />\n                </button>\n              </DropdownMenuTrigger>\n              <DropdownMenuContent className=\"bg-[#2B2D31] border border-[#1E1F22] p-1 rounded-lg\">\n                <div className=\"flex gap-1\">\n                  {REACTION_EMOJIS.map(e => (\n                    <DropdownMenuItem key={e.id} onClick={() => reaction.toggle(e.id)} className=\"cursor-pointer hover:bg-[#383A40] rounded p-1.5 justify-center\">\n                      <span className=\"text-lg leading-none\">{e.emoji}</span>\n                    </DropdownMenuItem>\n                  ))}\n                </div>\n              </DropdownMenuContent>\n            </DropdownMenu>\n          </div>\n        </div>\n        {/* Hover actions */}\n        <div className=\"opacity-0 group-hover:opacity-100 absolute right-2 -top-4 flex bg-[#2B2D31] border border-[#1E1F22] rounded-lg shadow-xl overflow-hidden transition-all\">\n          <button onClick={() => reaction.toggle('like')} className=\"px-2.5 py-1.5 hover:bg-[#36373D] text-sm transition-colors\">üëç</button>\n          {isAuthor && <button onClick={() => edit.startEditing(comment.content)} className=\"px-2.5 py-1.5 hover:bg-[#36373D] text-sm transition-colors\">‚úèÔ∏è</button>}\n          {isAuthor && <button onClick={deleteComment} className=\"px-2.5 py-1.5 hover:bg-[#36373D] text-sm text-red-400 transition-colors\">üóëÔ∏è</button>}\n        </div>\n      </div>\n      {replies.map(r => (\n        <DiscordReply key={r.id} comment={r} tree={tree} currentUser={currentUser} />\n      ))}\n    </>\n  );\n};\n\nconst DiscordReply = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { reaction } = useComment(comment, {\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n  const nameColor = roleColors[comment.author.name] || '#FFFFFF';\n\n  return (\n    <div className=\"ml-14 flex gap-3 hover:bg-[#2E3035]/50 px-4 py-1 -mx-4 rounded-md transition-colors group/reply\">\n      <img src={comment.author.avatarUrl} alt=\"\" className=\"w-6 h-6 rounded-full mt-0.5 shrink-0\" />\n      <div className=\"flex-1\">\n        <div className=\"flex items-baseline gap-2\">\n          <span className=\"text-sm font-medium\" style={{ color: nameColor }}>{comment.author.name}</span>\n          <span className=\"text-[11px] text-[#949BA4]\">{formatRelativeTime(comment.createdAt)}</span>\n        </div>\n        <p className=\"text-sm text-[#DBDEE1]\">{comment.content}</p>\n        {comment.reactions && comment.reactions.filter(r => r.count > 0).length > 0 && (\n          <div className=\"flex flex-wrap gap-1 mt-1\">\n            {comment.reactions.filter(r => r.count > 0).map(r => (\n              <button key={r.id} onClick={() => reaction.toggle(r.id)} className={`flex items-center gap-1 text-[11px] px-1.5 py-0.5 rounded-md transition-colors ${r.isActive ? 'bg-[#5865F2]/20 border border-[#5865F2]/50 text-[#DEE0FC]' : 'bg-[#2B2D31] border border-transparent hover:border-[#4E5058] text-[#B5BAC1]'}`}>\n                {r.emoji} {r.count}\n              </button>\n            ))}\n          </div>\n        )}\n      </div>\n    </div>\n  );\n};\n\nconst DiscordStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#313338] text-[#DBDEE1] p-5 rounded-2xl\">\n      <div className=\"flex items-center gap-2.5 border-b border-[#3F4147] pb-3 mb-4\">\n        <Hash className=\"w-5 h-5 text-[#949BA4]\" />\n        <span className=\"text-base font-semibold text-white\">general</span>\n        <span className=\"text-xs text-[#949BA4] border-l border-[#3F4147] pl-2.5 ml-0.5\">Free-form conversation</span>\n      </div>\n      <div className=\"space-y-1\">\n        {tree.comments.map(c => (\n          <div key={c.id} className=\"relative\">\n            <DiscordMessage comment={c} tree={tree} currentUser={currentUser} />\n          </div>\n        ))}\n      </div>\n      <div className=\"mt-4\">\n        <div className=\"bg-[#383A40] rounded-xl overflow-hidden\">\n          <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Message #general\" className=\"w-full bg-transparent px-4 py-3 text-sm text-[#DBDEE1] placeholder:text-[#6D6F78] outline-none\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n        </div>\n      </div>\n    </div>\n  );\n};\n\nexport default DiscordStyle;\n";

export const FacebookStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { ThumbsUp, MessageCircle } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst FBComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div className={`${depth > 0 ? 'ml-12' : ''} mb-3`}>\n      <div className=\"flex gap-2.5\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-9 h-9 rounded-full mt-0.5 shrink-0 ring-1 ring-white/5\" />\n        <div className=\"flex-1\">\n          <div className=\"bg-[#3A3B3C] rounded-2xl px-3.5 py-2.5 inline-block max-w-full relative group\">\n            <span className=\"text-[13px] font-semibold text-[#E4E6EB] block\">{comment.author.name}</span>\n            {edit.isEditing ? (\n              <div className=\"mt-1\">\n                <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#242526] rounded-lg p-2.5 text-sm text-[#E4E6EB] outline-none border border-[#4E4F50] focus:border-[#2D88FF] transition-colors\" />\n                <div className=\"flex gap-2 mt-1.5\">\n                  <button onClick={edit.submitEdit} className=\"text-xs text-[#2D88FF] font-semibold\">Save</button>\n                  <button onClick={edit.cancelEdit} className=\"text-xs text-[#B0B3B8]\">Cancel</button>\n                </div>\n              </div>\n            ) : (\n              <p className=\"text-[15px] text-[#E4E6EB] leading-snug\">{comment.content}</p>\n            )}\n            {(likeReaction?.count ?? 0) > 0 && (\n              <div className=\"absolute -bottom-2.5 right-3 bg-[#3A3B3C] rounded-full px-2 py-0.5 flex items-center gap-1 shadow-lg border border-white/5\">\n                <div className=\"w-4 h-4 rounded-full bg-[#2D88FF] flex items-center justify-center\">\n                  <ThumbsUp className=\"w-2.5 h-2.5 text-white\" fill=\"white\" />\n                </div>\n                <span className=\"text-[11px] text-[#B0B3B8] font-medium\">{likeReaction?.count}</span>\n              </div>\n            )}\n          </div>\n          {!edit.isEditing && (\n            <div className=\"flex items-center gap-4 mt-1.5 ml-3 text-xs font-semibold text-[#B0B3B8]\">\n              <div className=\"relative group\">\n                <button onClick={() => reaction.toggle('like')} className={`hover:underline transition-colors ${likeReaction?.isActive ? 'text-[#2D88FF]' : ''}`}>Like</button>\n                <div className=\"absolute bottom-6 left-0 bg-white shadow-xl rounded-full px-2 py-1 flex gap-1 transform scale-0 group-hover:scale-100 transition-transform origin-bottom-left z-20 border border-black/5\">\n                  <button onClick={() => reaction.toggle('like')} className=\"text-xl hover:scale-125 transition-transform p-1\">üëç</button>\n                  <button onClick={() => reaction.toggle('love')} className=\"text-xl hover:scale-125 transition-transform p-1\">‚ù§Ô∏è</button>\n                  <button onClick={() => reaction.toggle('haha')} className=\"text-xl hover:scale-125 transition-transform p-1\">üòÇ</button>\n                  <button onClick={() => reaction.toggle('wow')} className=\"text-xl hover:scale-125 transition-transform p-1\">üòÆ</button>\n                  <button onClick={() => reaction.toggle('sad')} className=\"text-xl hover:scale-125 transition-transform p-1\">üò¢</button>\n                  <button onClick={() => reaction.toggle('angry')} className=\"text-xl hover:scale-125 transition-transform p-1\">üò°</button>\n                </div>\n              </div>\n              <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"hover:underline\">Reply</button>\n              {isAuthor && <button onClick={() => edit.startEditing(comment.content)} className=\"hover:underline\">Edit</button>}\n              {isAuthor && <button onClick={deleteComment} className=\"hover:underline text-red-400/80\">Delete</button>}\n              <span className=\"text-[#65676B] font-normal\">{formatRelativeTime(comment.createdAt)}</span>\n            </div>\n          )}\n          {reply.isReplying && (\n            <div className=\"flex gap-2 mt-2.5 items-center\">\n              <img src={currentUser.avatarUrl} alt=\"\" className=\"w-7 h-7 rounded-full ring-1 ring-white/5\" />\n              <div className=\"flex-1 bg-[#3A3B3C] rounded-full px-4 flex items-center\">\n                <input value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder={`Reply to ${comment.author.name}...`} className=\"flex-1 bg-transparent py-2 text-sm text-[#E4E6EB] placeholder:text-[#B0B3B8] outline-none\" onKeyDown={e => { if (e.key === 'Enter') reply.submitReply(); }} autoFocus />\n                <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-xs font-semibold text-[#2D88FF] disabled:opacity-40\">Post</button>\n              </div>\n            </div>\n          )}\n          {replies.length > 0 && (\n            <>\n              {!showReplies && (\n                <button onClick={toggleReplies} className=\"flex items-center gap-1.5 text-xs font-semibold text-[#B0B3B8] mt-2 ml-3 hover:underline\">\n                  <MessageCircle className=\"w-3.5 h-3.5\" /> {replies.length} replies\n                </button>\n              )}\n              {showReplies && (\n                <div className=\"mt-2\">\n                  {replies.map(r => <FBComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n                  <button onClick={toggleReplies} className=\"text-xs text-[#B0B3B8] ml-3 hover:underline font-semibold\">Hide replies</button>\n                </div>\n              )}\n            </>\n          )}\n        </div>\n      </div>\n    </div>\n  );\n};\n\nconst FacebookStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#242526] text-[#E4E6EB] p-5 rounded-2xl\">\n      <div className=\"flex gap-2.5 mb-5 items-center\">\n        <img src={currentUser.avatarUrl} alt=\"\" className=\"w-9 h-9 rounded-full ring-1 ring-white/5\" />\n        <div className=\"flex-1 bg-[#3A3B3C] rounded-full px-4 flex items-center\">\n          <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Write a comment...\" className=\"flex-1 bg-transparent py-2.5 text-sm text-[#E4E6EB] placeholder:text-[#B0B3B8] outline-none\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n        </div>\n      </div>\n      {tree.comments.map(c => <FBComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n    </div>\n  );\n};\n\nexport default FacebookStyle;\n";

export const GitHubStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { Smile } from 'lucide-react';\nimport { useState } from 'react';\nimport {\n  DropdownMenu,\n  DropdownMenuContent,\n  DropdownMenuItem,\n  DropdownMenuTrigger,\n} from '@/components/ui/dropdown-menu';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst REACTION_EMOJIS = [\n  { id: 'like', emoji: 'üëç' },\n  { id: 'dislike', emoji: 'üëé' },\n  { id: 'love', emoji: '‚ù§Ô∏è' },\n  { id: 'haha', emoji: 'üòÇ' },\n  { id: 'wow', emoji: 'üòÆ' },\n  { id: 'sad', emoji: 'üò¢' },\n  { id: 'angry', emoji: 'üò°' },\n];\n\nconst GHComment = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { isAuthor, edit, reply, reaction, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const replies = comment.replies || [];\n\n  return (\n    <div className=\"mb-4\">\n      <div className=\"border border-[#30363D] rounded-xl overflow-hidden\">\n        <div className=\"flex items-center justify-between bg-[#161B22] px-4 py-2.5 border-b border-[#30363D]\">\n          <div className=\"flex items-center gap-2.5 text-sm\">\n            <img src={comment.author.avatarUrl} alt=\"\" className=\"w-6 h-6 rounded-full ring-1 ring-white/10\" />\n            <span className=\"font-semibold text-[#E6EDF3] hover:underline cursor-pointer\">{comment.author.name}</span>\n            <span className=\"text-[#8B949E]\">commented {formatRelativeTime(comment.createdAt)}</span>\n          </div>\n          {isAuthor && (\n            <div className=\"flex gap-1.5\">\n              <span className=\"text-[10px] font-medium text-[#8B949E] border border-[#30363D] rounded-full px-2 py-0.5\">Author</span>\n              <button onClick={() => edit.startEditing(comment.content)} className=\"text-xs text-[#8B949E] hover:text-[#58A6FF] transition-colors\">Edit</button>\n              <button onClick={deleteComment} className=\"text-xs text-[#8B949E] hover:text-red-400 transition-colors\">Delete</button>\n            </div>\n          )}\n        </div>\n        <div className=\"bg-[#0D1117] px-4 py-4\">\n          {edit.isEditing ? (\n            <div>\n              <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#0D1117] border border-[#30363D] rounded-lg p-3 text-sm text-[#E6EDF3] font-mono min-h-[100px] focus:border-[#58A6FF] outline-none transition-colors\" />\n              <div className=\"flex gap-2 mt-3\">\n                <button onClick={edit.submitEdit} className=\"text-xs bg-[#238636] text-white px-4 py-2 rounded-lg font-medium hover:bg-[#2EA043] transition-colors\">Update comment</button>\n                <button onClick={edit.cancelEdit} className=\"text-xs text-[#8B949E] px-4 py-2 rounded-lg border border-[#30363D] hover:border-[#8B949E] transition-colors\">Cancel</button>\n              </div>\n            </div>\n          ) : (\n            <p className=\"text-sm text-[#E6EDF3] leading-relaxed\">{comment.content}</p>\n          )}\n        </div>\n        <div className=\"bg-[#0D1117] px-4 py-2.5 border-t border-[#30363D] flex items-center gap-2\">\n          {comment.reactions?.filter(r => r.count > 0).map(r => (\n            <button key={r.id} onClick={() => reaction.toggle(r.id)} className={`flex items-center gap-1.5 text-xs px-2.5 py-1.5 rounded-full border transition-colors ${r.isActive ? 'border-[#58A6FF]/50 bg-[#58A6FF]/10 text-[#58A6FF]' : 'border-[#30363D] text-[#8B949E] hover:border-[#8B949E]'}`}>\n              {r.emoji} {r.count}\n            </button>\n          ))}\n          <DropdownMenu>\n            <DropdownMenuTrigger asChild>\n              <button className=\"flex items-center gap-1 text-xs px-2.5 py-1.5 rounded-full border border-[#30363D] text-[#8B949E] hover:border-[#8B949E] transition-colors\">\n                <Smile className=\"w-3.5 h-3.5\" />\n              </button>\n            </DropdownMenuTrigger>\n            <DropdownMenuContent className=\"bg-[#161B22] border border-[#30363D] p-1 rounded-lg\">\n              <div className=\"flex gap-1\">\n                {REACTION_EMOJIS.map(e => (\n                  <DropdownMenuItem key={e.id} onClick={() => reaction.toggle(e.id)} className=\"cursor-pointer hover:bg-[#30363D] rounded p-1.5 justify-center\">\n                    <span className=\"text-lg leading-none\">{e.emoji}</span>\n                  </DropdownMenuItem>\n                ))}\n              </div>\n            </DropdownMenuContent>\n          </DropdownMenu>\n        </div>\n      </div>\n      {replies.map(r => (\n        <GHReply key={r.id} comment={r} tree={tree} currentUser={currentUser} />\n      ))}\n    </div>\n  );\n};\n\nconst GHReply = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { reaction } = useComment(comment, {\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n\n  return (\n    <div className=\"ml-10 mt-3 relative before:absolute before:left-[-20px] before:top-0 before:bottom-0 before:w-0.5 before:bg-[#30363D] before:rounded-full\">\n      <div className=\"border border-[#30363D] rounded-xl overflow-hidden\">\n        <div className=\"flex items-center bg-[#161B22] px-3.5 py-2 border-b border-[#30363D] text-xs gap-2.5\">\n          <img src={comment.author.avatarUrl} alt=\"\" className=\"w-5 h-5 rounded-full\" />\n          <span className=\"font-semibold text-[#E6EDF3]\">{comment.author.name}</span>\n          <span className=\"text-[#8B949E]\">{formatRelativeTime(comment.createdAt)}</span>\n        </div>\n        <div className=\"bg-[#0D1117] px-3.5 py-3\">\n          <p className=\"text-sm text-[#E6EDF3]\">{comment.content}</p>\n        </div>\n        {comment.reactions && comment.reactions.filter(r => r.count > 0).length > 0 && (\n          <div className=\"bg-[#0D1117] px-3.5 py-2 border-t border-[#30363D] flex items-center gap-1.5\">\n            {comment.reactions.filter(r => r.count > 0).map(r => (\n              <button key={r.id} onClick={() => reaction.toggle(r.id)} className={`flex items-center gap-1 text-[11px] px-2 py-1 rounded-full border transition-colors ${r.isActive ? 'border-[#58A6FF]/50 bg-[#58A6FF]/10 text-[#58A6FF]' : 'border-[#30363D] text-[#8B949E] hover:border-[#8B949E]'}`}>\n                {r.emoji} {r.count}\n              </button>\n            ))}\n          </div>\n        )}\n      </div>\n    </div>\n  );\n};\n\nconst GitHubStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#0D1117] text-[#E6EDF3] p-5 rounded-2xl\">\n      {tree.comments.map(c => <GHComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n      <div className=\"border border-[#30363D] rounded-xl overflow-hidden mt-5\">\n        <div className=\"bg-[#161B22] px-4 py-2.5 border-b border-[#30363D] flex items-center gap-3\">\n          <button className=\"text-xs text-[#E6EDF3] font-medium border-b-2 border-[#F78166] pb-1.5 -mb-[11px]\">Write</button>\n          <button className=\"text-xs text-[#8B949E] hover:text-[#E6EDF3] transition-colors\">Preview</button>\n        </div>\n        <textarea value={text} onChange={e => setText(e.target.value)} placeholder=\"Leave a comment\" className=\"w-full bg-[#0D1117] px-4 py-3 text-sm text-[#E6EDF3] placeholder:text-[#484F58] outline-none min-h-[100px] font-mono\" />\n        <div className=\"bg-[#161B22] px-4 py-3 border-t border-[#30363D] flex justify-end\">\n          <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-sm bg-[#238636] text-white px-4 py-2 rounded-lg font-medium hover:bg-[#2EA043] disabled:opacity-40 transition-colors\">Comment</button>\n        </div>\n      </div>\n    </div>\n  );\n};\n\nexport default GitHubStyle;\n";

export const HackerNewsStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { ChevronUp } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst HNComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div className={depth > 0 ? 'ml-5 pl-4 border-l-2 border-[#FF6600]/10' : ''}>\n      <div className=\"py-2\">\n        <div className=\"flex items-center gap-1.5 text-xs mb-1\">\n          <button onClick={() => reaction.toggle('like')} className={`hover:text-[#FF6600] transition-colors ${likeReaction?.isActive ? 'text-[#FF6600]' : 'text-[#828282]'}`}>\n            <ChevronUp className=\"w-4 h-4\" />\n          </button>\n          <span className=\"text-[#FF6600] font-medium hover:underline cursor-pointer\">{comment.author.name}</span>\n          <span className=\"text-[#828282]\">{formatRelativeTime(comment.createdAt)}</span>\n          {(likeReaction?.count ?? 0) > 0 && <span className=\"text-[#828282]\">| {likeReaction?.count} points</span>}\n        </div>\n        <p className=\"text-[13px] text-[#828282] leading-relaxed ml-5\">{comment.content}</p>\n        <div className=\"flex items-center gap-3 ml-5 mt-1 text-xs text-[#828282]\">\n          <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"hover:underline hover:text-[#FF6600] transition-colors\">reply</button>\n          {isAuthor && <button onClick={deleteComment} className=\"hover:underline hover:text-red-500 transition-colors\">delete</button>}\n          {replies.length > 0 && <button onClick={toggleReplies} className=\"hover:underline hover:text-[#FF6600] transition-colors\">[{showReplies ? '‚àí' : '+'}] {replies.length}</button>}\n        </div>\n        {reply.isReplying && (\n          <div className=\"ml-5 mt-2.5\">\n            <textarea value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} className=\"w-full bg-white text-[#222] text-sm p-2.5 border border-[#ccc] rounded font-mono min-h-[60px] focus:border-[#FF6600] outline-none transition-colors\" autoFocus />\n            <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-xs bg-[#F6F6EF] border border-[#ccc] px-3 py-1.5 mt-1.5 hover:bg-[#E6E6DF] disabled:opacity-40 rounded transition-colors\">reply</button>\n          </div>\n        )}\n      </div>\n      {showReplies && replies.map(r => <HNComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n    </div>\n  );\n};\n\nconst HackerNewsStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#F6F6EF] text-[#000] p-5 rounded-2xl\" style={{ fontFamily: 'Verdana, Geneva, sans-serif' }}>\n      <div className=\"bg-[#FF6600] px-3 py-2 mb-5 flex items-center gap-2.5 rounded-lg\">\n        <span className=\"text-sm font-bold text-white border border-white w-5 h-5 flex items-center justify-center\">Y</span>\n        <span className=\"text-[13px] font-bold text-white\">Hacker News</span>\n      </div>\n      <div className=\"mb-5\">\n        <textarea value={text} onChange={e => setText(e.target.value)} placeholder=\"Add comment...\" className=\"w-full bg-white border border-[#ccc] text-sm text-[#222] p-2.5 font-mono min-h-[60px] rounded focus:border-[#FF6600] outline-none transition-colors\" />\n        <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-xs bg-[#F6F6EF] border border-[#ccc] px-4 py-1.5 mt-1.5 hover:bg-[#E6E6DF] disabled:opacity-40 rounded transition-colors\">add comment</button>\n      </div>\n      {tree.comments.map(c => <HNComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n    </div>\n  );\n};\n\nexport default HackerNewsStyle;\n";

export const InstagramStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { Heart } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst IGComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div className={`${depth > 0 ? 'ml-14' : ''} mb-4`}>\n      <div className=\"flex gap-3 items-start\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-9 h-9 rounded-full ring-2 ring-gradient-to-br ring-pink-500/20\" />\n        <div className=\"flex-1 min-w-0\">\n          <p className=\"text-[14px] leading-snug\">\n            <span className=\"font-semibold text-white mr-2\">{comment.author.name}</span>\n            <span className=\"text-[#E0E0E0]\">{comment.content}</span>\n          </p>\n          <div className=\"flex items-center gap-4 mt-1.5 text-xs text-[#A8A8A8]\">\n            <span>{formatRelativeTime(comment.createdAt)}</span>\n            {(likeReaction?.count ?? 0) > 0 && <span className=\"font-semibold\">{likeReaction?.count} likes</span>}\n            <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"font-semibold hover:text-white transition-colors\">Reply</button>\n            {isAuthor && <button onClick={deleteComment} className=\"font-semibold text-red-400/80 hover:text-red-400 transition-colors\">Delete</button>}\n          </div>\n        </div>\n        <button onClick={() => reaction.toggle('like')} className=\"mt-2 shrink-0 active:scale-125 transition-transform\">\n          <Heart className={`w-4 h-4 transition-colors ${likeReaction?.isActive ? 'text-[#ED4956] fill-[#ED4956]' : 'text-[#A8A8A8] hover:text-[#ED4956]'}`} />\n        </button>\n      </div>\n      {reply.isReplying && (\n        <div className=\"ml-12 mt-2.5 flex gap-2\">\n          <input value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder={`Reply to @${comment.author.name}...`} className=\"flex-1 bg-transparent border-b border-[#363636] pb-1.5 text-sm text-white placeholder:text-[#A8A8A8] outline-none focus:border-[#A8A8A8] transition-colors\" autoFocus onKeyDown={e => { if (e.key === 'Enter') reply.submitReply(); }} />\n          <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-xs font-bold text-[#0095F6] disabled:opacity-40\">Post</button>\n        </div>\n      )}\n      {replies.length > 0 && (\n        <div className=\"ml-14 mt-3\">\n          {!showReplies && (\n            <button onClick={toggleReplies} className=\"text-xs text-[#A8A8A8] font-semibold flex items-center gap-3 group\">\n              <div className=\"w-6 h-px bg-[#A8A8A8] group-hover:w-8 transition-all\" />\n              View {replies.length} replies\n            </button>\n          )}\n          {showReplies && (\n            <>\n              {replies.map(r => <IGComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n              <button onClick={toggleReplies} className=\"text-xs text-[#A8A8A8] font-semibold mt-1\">Hide replies</button>\n            </>\n          )}\n        </div>\n      )}\n    </div>\n  );\n};\n\nconst InstagramStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-black text-white p-5 rounded-2xl font-sans\">\n      <div className=\"space-y-0\">\n        {tree.comments.map(c => <IGComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n      </div>\n      <div className=\"flex items-center gap-3 mt-4 pt-4 border-t border-[#262626]\">\n        <img src={currentUser.avatarUrl} alt=\"\" className=\"w-9 h-9 rounded-full\" />\n        <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Add a comment...\" className=\"flex-1 bg-transparent text-sm text-white placeholder:text-[#A8A8A8] outline-none\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n        <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-sm font-bold text-[#0095F6] disabled:opacity-40 hover:text-white transition-colors\">Post</button>\n      </div>\n    </div>\n  );\n};\n\nexport default InstagramStyle;\n";

export const LinkedInStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { ThumbsUp, MessageCircle, Globe, Send } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst LIComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div className={`${depth > 0 ? 'ml-12' : ''} mb-1`}>\n      <div className=\"flex gap-3 py-3\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full shrink-0 ring-1 ring-white/5\" />\n        <div className=\"flex-1\">\n          <div className=\"mb-0.5\">\n            <span className=\"text-sm font-semibold text-white hover:text-[#70B5F9] cursor-pointer transition-colors\">{comment.author.name}</span>\n            <p className=\"text-xs text-[#FFFFFF99] leading-snug\">Software Engineer ¬∑ 3rd+</p>\n            <span className=\"text-[11px] text-[#FFFFFF66] flex items-center gap-1\">{formatRelativeTime(comment.createdAt)} ¬∑ <Globe className=\"w-3 h-3 inline\" /></span>\n          </div>\n          {edit.isEditing ? (\n            <div className=\"mt-2\">\n              <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#1D2226] border border-[#38434F] rounded-lg p-3 text-sm text-white outline-none focus:border-[#0A66C2] transition-colors\" />\n              <div className=\"flex gap-2 mt-2\">\n                <button onClick={edit.submitEdit} className=\"text-xs bg-[#0A66C2] text-white px-4 py-1.5 rounded-full font-medium hover:bg-[#004182] transition-colors\">Save</button>\n                <button onClick={edit.cancelEdit} className=\"text-xs text-[#FFFFFF99] hover:text-white transition-colors\">Cancel</button>\n              </div>\n            </div>\n          ) : (\n            <p className=\"text-[14px] text-[#FFFFFFE6] mt-1.5 leading-relaxed\">{comment.content}</p>\n          )}\n          {(likeReaction?.count ?? 0) > 0 && (\n            <div className=\"flex items-center gap-1.5 mt-2 pb-2 border-b border-[#38434F]/50\">\n              <span className=\"text-sm\">üëç</span>\n              <span className=\"text-xs text-[#FFFFFF99]\">{likeReaction?.count}</span>\n            </div>\n          )}\n          {!edit.isEditing && (\n            <div className=\"flex items-center gap-1 mt-1.5 -ml-2\">\n              <div className=\"relative group\">\n                <button onClick={() => reaction.toggle('like')} className={`flex items-center gap-1.5 text-xs px-3 py-2 rounded-md hover:bg-white/5 font-semibold transition-colors ${likeReaction?.isActive ? 'text-[#0A66C2]' : 'text-[#FFFFFF99]'}`}>\n                  <ThumbsUp className=\"w-4 h-4\" /> Like\n                </button>\n                <div className=\"absolute bottom-10 left-[-10px] bg-white text-black shadow-xl rounded-full px-3 py-1.5 flex gap-2 transform scale-0 group-hover:scale-100 transition-all origin-bottom-left z-20 border border-white/20\">\n                  <button onClick={() => reaction.toggle('like')} className=\"text-xl hover:scale-125 transition-transform\">üëç</button>\n                  <button onClick={() => reaction.toggle('love')} className=\"text-xl hover:scale-125 transition-transform\">‚ù§Ô∏è</button>\n                  <button onClick={() => reaction.toggle('haha')} className=\"text-xl hover:scale-125 transition-transform\">üòÇ</button>\n                  <button onClick={() => reaction.toggle('wow')} className=\"text-xl hover:scale-125 transition-transform\">üí°</button>\n                  <button onClick={() => reaction.toggle('sad')} className=\"text-xl hover:scale-125 transition-transform\">ü§î</button>\n                </div>\n              </div>\n              <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"flex items-center gap-1.5 text-xs text-[#FFFFFF99] px-3 py-2 rounded-md hover:bg-white/5 font-semibold transition-colors\">\n                <MessageCircle className=\"w-4 h-4\" /> Comment\n              </button>\n              {isAuthor && (\n                <>\n                  <button onClick={() => edit.startEditing(comment.content)} className=\"text-xs text-[#FFFFFF99] px-3 py-2 rounded-md hover:bg-white/5 transition-colors\">Edit</button>\n                  <button onClick={deleteComment} className=\"text-xs text-red-400/70 px-3 py-2 rounded-md hover:bg-red-400/10 transition-colors\">Delete</button>\n                </>\n              )}\n            </div>\n          )}\n          {reply.isReplying && (\n            <div className=\"flex gap-2.5 mt-3\">\n              <img src={currentUser.avatarUrl} alt=\"\" className=\"w-8 h-8 rounded-full ring-1 ring-white/5\" />\n              <div className=\"flex-1 border border-[#38434F] rounded-full flex items-center px-4 focus-within:border-[#0A66C2] transition-colors\">\n                <input value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"Add a reply...\" className=\"flex-1 bg-transparent text-sm text-white placeholder:text-[#FFFFFF66] outline-none py-2.5\" onKeyDown={e => { if (e.key === 'Enter') reply.submitReply(); }} autoFocus />\n                <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-[#0A66C2] disabled:opacity-40 transition-colors\">\n                  <Send className=\"w-4 h-4\" />\n                </button>\n              </div>\n            </div>\n          )}\n          {replies.length > 0 && (\n            <>\n              {!showReplies && <button onClick={toggleReplies} className=\"text-xs font-semibold text-[#FFFFFF99] mt-2 hover:text-white hover:underline transition-colors\">{replies.length} replies</button>}\n              {showReplies && (\n                <div className=\"mt-2\">\n                  {replies.map(r => <LIComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n                  <button onClick={toggleReplies} className=\"text-xs text-[#FFFFFF99] hover:underline transition-colors\">Hide replies</button>\n                </div>\n              )}\n            </>\n          )}\n        </div>\n      </div>\n    </div>\n  );\n};\n\nconst LinkedInStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#1B1F23] text-white p-5 rounded-2xl\">\n      <div className=\"flex gap-2.5 mb-5\">\n        <img src={currentUser.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full ring-1 ring-white/5\" />\n        <div className=\"flex-1 border border-[#38434F] rounded-full flex items-center px-4 focus-within:border-[#0A66C2] transition-colors\">\n          <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Add a comment...\" className=\"flex-1 bg-transparent text-sm text-white placeholder:text-[#FFFFFF66] outline-none py-3\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n          <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-[#0A66C2] disabled:opacity-40 transition-colors\">\n            <Send className=\"w-4 h-4\" />\n          </button>\n        </div>\n      </div>\n      {tree.comments.map(c => <LIComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n    </div>\n  );\n};\n\nexport default LinkedInStyle;\n";

export const MediumStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst MediumComment = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div className=\"py-6 border-b border-[#E6E6E6]/80 last:border-0\">\n      <div className=\"flex items-center gap-3 mb-3\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full\" />\n        <div>\n          <span className=\"text-sm font-medium text-[#242424] block hover:underline cursor-pointer\">{comment.author.name}</span>\n          <span className=\"text-xs text-[#6B6B6B]\">{formatRelativeTime(comment.createdAt)}</span>\n        </div>\n      </div>\n      {edit.isEditing ? (\n        <div>\n          <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full border border-[#E6E6E6] rounded-lg p-4 text-[16px] text-[#242424] leading-[1.8] focus:border-[#1A8917] outline-none transition-colors\" style={{ fontFamily: 'Georgia, \"Times New Roman\", serif' }} />\n          <div className=\"flex gap-2 mt-3\">\n            <button onClick={edit.submitEdit} className=\"text-sm bg-[#1A8917] text-white px-5 py-2 rounded-full font-medium hover:bg-[#0F730C] transition-colors\">Save</button>\n            <button onClick={edit.cancelEdit} className=\"text-sm text-[#6B6B6B] hover:text-[#242424] transition-colors\">Cancel</button>\n          </div>\n        </div>\n      ) : (\n        <p className=\"text-[16px] text-[#242424] leading-[1.8]\" style={{ fontFamily: 'Georgia, \"Times New Roman\", serif' }}>{comment.content}</p>\n      )}\n      {!edit.isEditing && (\n        <div className=\"flex items-center gap-5 mt-4\">\n          <button onClick={() => reaction.toggle('like')} className={`flex items-center gap-2 text-sm transition-colors ${likeReaction?.isActive ? 'text-[#1A8917]' : 'text-[#6B6B6B] hover:text-[#242424]'}`}>\n            <span className=\"text-xl active:scale-125 transition-transform\">üëè</span>\n            {(likeReaction?.count ?? 0) > 0 && <span className=\"font-medium\">{likeReaction?.count}</span>}\n          </button>\n          <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"text-sm text-[#6B6B6B] hover:text-[#242424] transition-colors\">Reply</button>\n          {isAuthor && <button onClick={() => edit.startEditing(comment.content)} className=\"text-sm text-[#6B6B6B] hover:text-[#242424] transition-colors\">Edit</button>}\n          {isAuthor && <button onClick={deleteComment} className=\"text-sm text-[#6B6B6B] hover:text-red-500 transition-colors\">Delete</button>}\n        </div>\n      )}\n      {reply.isReplying && (\n        <div className=\"mt-4 ml-13\">\n          <textarea value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"Write a response...\" className=\"w-full border-0 border-b-2 border-[#E6E6E6] text-[16px] text-[#242424] py-2 outline-none focus:border-[#1A8917] transition-colors\" style={{ fontFamily: 'Georgia, \"Times New Roman\", serif' }} autoFocus />\n          <div className=\"flex justify-end gap-3 mt-3\">\n            <button onClick={reply.cancelReply} className=\"text-sm text-[#6B6B6B] hover:text-[#242424] transition-colors\">Cancel</button>\n            <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-sm bg-[#1A8917] text-white px-5 py-2 rounded-full font-medium disabled:opacity-40 hover:bg-[#0F730C] transition-colors\">Respond</button>\n          </div>\n        </div>\n      )}\n      {replies.length > 0 && (\n        <>\n          <button onClick={toggleReplies} className=\"text-sm text-[#1A8917] font-medium mt-4 hover:text-[#0F730C] transition-colors\">\n            {showReplies ? 'Hide' : 'View'} {replies.length} responses\n          </button>\n          {showReplies && (\n            <div className=\"ml-13 mt-3 border-l-2 border-[#E6E6E6] pl-5\">\n              {replies.map(r => (\n                <MediumReply key={r.id} comment={r} tree={tree} currentUser={currentUser} />\n              ))}\n            </div>\n          )}\n        </>\n      )}\n    </div>\n  );\n};\n\nconst MediumReply = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { reaction } = useComment(comment, {\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n\n  return (\n    <div className=\"py-4\">\n      <div className=\"flex items-center gap-2.5 mb-2\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-7 h-7 rounded-full\" />\n        <span className=\"text-sm font-medium text-[#242424]\">{comment.author.name}</span>\n        <span className=\"text-xs text-[#6B6B6B]\">{formatRelativeTime(comment.createdAt)}</span>\n      </div>\n      <p className=\"text-[15px] text-[#242424] leading-relaxed\" style={{ fontFamily: 'Georgia, \"Times New Roman\", serif' }}>{comment.content}</p>\n      <button onClick={() => reaction.toggle('like')} className={`flex items-center gap-1.5 mt-2 text-sm transition-colors ${likeReaction?.isActive ? 'text-[#1A8917]' : 'text-[#6B6B6B] hover:text-[#242424]'}`}>\n        <span className=\"text-base active:scale-125 transition-transform\">üëè</span>\n        {(likeReaction?.count ?? 0) > 0 && <span className=\"text-xs font-medium\">{likeReaction?.count}</span>}\n      </button>\n    </div>\n  );\n};\n\nconst MediumStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-white text-[#242424] p-6 rounded-2xl\" style={{ fontFamily: '-apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif' }}>\n      <h3 className=\"text-xl font-bold mb-5 tracking-tight\">Responses ({tree.totalCount})</h3>\n      <div className=\"mb-5 border border-[#E6E6E6] rounded-xl p-5\">\n        <div className=\"flex gap-3 items-start\">\n          <img src={currentUser.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full\" />\n          <div className=\"flex-1\">\n            <textarea value={text} onChange={e => setText(e.target.value)} placeholder=\"What are your thoughts?\" className=\"w-full text-[16px] text-[#242424] placeholder:text-[#B3B3B3] outline-none resize-none min-h-[70px]\" style={{ fontFamily: 'Georgia, \"Times New Roman\", serif' }} />\n            <div className=\"flex justify-end mt-2\">\n              <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-sm bg-[#1A8917] text-white px-5 py-2 rounded-full font-medium disabled:opacity-40 hover:bg-[#0F730C] transition-colors\">Respond</button>\n            </div>\n          </div>\n        </div>\n      </div>\n      {tree.comments.map(c => <MediumComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n    </div>\n  );\n};\n\nexport default MediumStyle;\n";

export const RedditStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { useSortedComments } from '@hasthiya_/headless-comments-react/headless';\nimport { ArrowBigUp, ArrowBigDown, MessageSquare } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst RedditComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const dislikeReaction = comment.reactions?.find(r => r.id === 'dislike');\n  const netScore = (likeReaction?.count ?? 0) - (dislikeReaction?.count ?? 0);\n  const replies = comment.replies || [];\n\n  return (\n    <div className={depth > 0 ? 'ml-3 pl-4 border-l-[2px] border-white/[0.08] hover:border-white/20 transition-colors' : ''}>\n      <div className=\"py-2.5 group\">\n        <div className=\"flex items-center gap-2 text-xs mb-1.5\">\n          <img src={comment.author.avatarUrl} alt=\"\" className=\"w-6 h-6 rounded-full ring-1 ring-white/10\" />\n          <span className=\"font-semibold text-[#D7DADC]\">{comment.author.name}</span>\n          <span className=\"text-[#818384]\">¬∑ {formatRelativeTime(comment.createdAt)}</span>\n        </div>\n        {edit.isEditing ? (\n          <div className=\"ml-8\">\n            <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#1A1A1B] border border-[#343536] rounded-xl p-3 text-sm text-[#D7DADC] focus:border-[#FF4500]/50 outline-none transition-colors\" />\n            <div className=\"flex gap-2 mt-2\">\n              <button onClick={edit.submitEdit} className=\"text-xs text-white bg-[#FF4500] px-4 py-1.5 rounded-full font-medium hover:bg-[#FF5722] transition-colors\">Save</button>\n              <button onClick={edit.cancelEdit} className=\"text-xs text-[#818384] hover:text-white transition-colors\">Cancel</button>\n            </div>\n          </div>\n        ) : (\n          <p className=\"text-[14px] text-[#D7DADC]/90 leading-relaxed mb-2 ml-8\">{comment.content}</p>\n        )}\n        {!edit.isEditing && (\n          <div className=\"flex items-center gap-0.5 text-xs text-[#818384] ml-7\">\n            <div className=\"flex items-center bg-[#272729] rounded-full\">\n              <button onClick={() => reaction.toggle('like')} className={`flex items-center gap-0.5 px-2 py-1.5 rounded-l-full hover:bg-[#FF4500]/10 transition-colors ${likeReaction?.isActive ? 'text-[#FF4500]' : ''}`}>\n                <ArrowBigUp className=\"w-5 h-5\" />\n              </button>\n              <span className={`text-xs font-medium px-1 ${netScore > 0 ? 'text-[#FF4500]' : netScore < 0 ? 'text-[#7193FF]' : ''}`}>{netScore}</span>\n              <button onClick={() => reaction.toggle('dislike')} className={`px-2 py-1.5 rounded-r-full hover:bg-[#7193FF]/10 transition-colors ${dislikeReaction?.isActive ? 'text-[#7193FF]' : ''}`}>\n                <ArrowBigDown className=\"w-5 h-5\" />\n              </button>\n            </div>\n            <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"flex items-center gap-1.5 px-3 py-1.5 rounded-full hover:bg-[#272729] transition-colors\">\n              <MessageSquare className=\"w-4 h-4\" /> Reply\n            </button>\n            {isAuthor && (\n              <>\n                <button onClick={() => edit.startEditing(comment.content)} className=\"px-3 py-1.5 hover:bg-[#272729] rounded-full transition-colors\">Edit</button>\n                <button onClick={deleteComment} className=\"px-3 py-1.5 hover:bg-red-500/10 rounded-full text-red-400 transition-colors\">Delete</button>\n              </>\n            )}\n          </div>\n        )}\n        {reply.isReplying && (\n          <div className=\"mt-3 ml-8\">\n            <textarea value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"What are your thoughts?\" className=\"w-full bg-[#1A1A1B] border border-[#343536] rounded-xl p-3 text-sm text-[#D7DADC] placeholder:text-[#818384] min-h-[80px] focus:border-[#FF4500]/50 outline-none transition-colors\" autoFocus />\n            <div className=\"flex gap-2 mt-2 justify-end\">\n              <button onClick={reply.cancelReply} className=\"text-xs text-[#818384] px-4 py-1.5 rounded-full hover:text-white transition-colors\">Cancel</button>\n              <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-xs bg-[#FF4500] text-white px-4 py-1.5 rounded-full font-medium disabled:opacity-40 hover:bg-[#FF5722] transition-colors\">Reply</button>\n            </div>\n          </div>\n        )}\n      </div>\n      {replies.length > 0 && (\n        <>\n          <button onClick={toggleReplies} className=\"text-xs text-[#FF4500] font-medium mb-1 ml-8 hover:text-[#FF5722] transition-colors\">{showReplies ? 'Hide' : 'Show'} {replies.length} replies</button>\n          {showReplies && replies.map(r => <RedditComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n        </>\n      )}\n    </div>\n  );\n};\n\nconst RedditStyle = ({ tree, currentUser }: Props) => {\n  const { sortedComments, sortOrder, setSortOrder } = useSortedComments(tree.comments, 'newest');\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#0E1113] text-[#D7DADC] p-5 rounded-2xl\">\n      <div className=\"flex gap-2 mb-5\">\n        {(['newest', 'oldest', 'popular'] as const).map(o => (\n          <button key={o} onClick={() => setSortOrder(o)} className={`text-xs px-4 py-1.5 rounded-full capitalize font-medium transition-all ${sortOrder === o ? 'bg-[#FF4500] text-white shadow-lg shadow-[#FF4500]/20' : 'text-[#818384] hover:bg-[#272729]'}`}>\n            {o === 'popular' ? 'top' : o}\n          </button>\n        ))}\n      </div>\n      <div className=\"mb-5\">\n        <div className=\"flex gap-3 items-start\">\n          <img src={currentUser.avatarUrl} alt=\"\" className=\"w-8 h-8 rounded-full ring-1 ring-white/10\" />\n          <div className=\"flex-1\">\n            <textarea value={text} onChange={e => setText(e.target.value)} placeholder=\"What are your thoughts?\" className=\"w-full bg-[#1A1A1B] border border-[#343536] rounded-xl p-3 text-sm text-[#D7DADC] placeholder:text-[#818384] min-h-[80px] focus:border-[#FF4500]/50 outline-none transition-colors\" />\n            <div className=\"flex justify-end mt-2\">\n              <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-xs font-semibold bg-[#FF4500] text-white px-5 py-2 rounded-full disabled:opacity-40 hover:bg-[#FF5722] transition-colors shadow-lg shadow-[#FF4500]/20\">Comment</button>\n            </div>\n          </div>\n        </div>\n      </div>\n      <div className=\"divide-y divide-white/[0.06]\">\n        {sortedComments.map(c => <RedditComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n      </div>\n    </div>\n  );\n};\n\nexport default RedditStyle;\n";

export const SlackStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { MessageSquare, Smile, Hash } from 'lucide-react';\nimport { useState } from 'react';\nimport {\n  DropdownMenu,\n  DropdownMenuContent,\n  DropdownMenuItem,\n  DropdownMenuTrigger,\n} from '@/components/ui/dropdown-menu';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst REACTION_EMOJIS = [\n  { id: 'like', emoji: 'üëç' },\n  { id: 'dislike', emoji: 'üëé' },\n  { id: 'love', emoji: '‚ù§Ô∏è' },\n  { id: 'haha', emoji: 'üòÇ' },\n  { id: 'wow', emoji: 'üòÆ' },\n  { id: 'sad', emoji: 'üò¢' },\n  { id: 'angry', emoji: 'üò°' },\n];\n\nconst SlackMessage = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const replies = comment.replies || [];\n\n  return (\n    <div className=\"group hover:bg-[#222529]/80 px-5 py-2 -mx-5 transition-colors relative\">\n      <div className=\"flex gap-2.5\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-9 h-9 rounded-lg mt-0.5 shrink-0\" />\n        <div className=\"flex-1 min-w-0\">\n          <div className=\"flex items-baseline gap-2\">\n            <span className=\"text-[15px] font-bold text-white\">{comment.author.name}</span>\n            <span className=\"text-[11px] text-[#ABABAD]\">{formatRelativeTime(comment.createdAt)}</span>\n          </div>\n          {edit.isEditing ? (\n            <div className=\"mt-1\">\n              <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#222529] border border-[#565856] rounded-lg p-2.5 text-sm text-white focus:border-[#007A5A] outline-none transition-colors\" />\n              <div className=\"flex gap-2 mt-1.5\">\n                <button onClick={edit.submitEdit} className=\"text-xs bg-[#007A5A] text-white px-3 py-1.5 rounded-md font-medium hover:bg-[#148567] transition-colors\">Save</button>\n                <button onClick={edit.cancelEdit} className=\"text-xs text-[#ABABAD] hover:text-white transition-colors\">Cancel</button>\n              </div>\n            </div>\n          ) : (\n            <p className=\"text-[15px] text-[#D1D2D3] leading-relaxed\">{comment.content}</p>\n          )}\n          <div className=\"flex flex-wrap items-center gap-1.5 mt-1.5\">\n            {comment.reactions?.filter(r => r.count > 0).map(r => (\n              <button key={r.id} onClick={() => reaction.toggle(r.id)} className={`flex items-center gap-1 text-xs px-2.5 py-1 rounded-full border transition-colors ${r.isActive ? 'border-[#007A5A]/50 bg-[#007A5A]/15 text-[#1D9F73]' : 'border-[#565856]/50 text-[#D1D2D3] hover:bg-[#222529]'}`}>\n                {r.emoji} {r.count}\n              </button>\n            ))}\n            <DropdownMenu>\n              <DropdownMenuTrigger asChild>\n                <button className=\"opacity-0 group-hover:opacity-100 p-1.5 rounded-md hover:bg-[#222529] text-[#ABABAD] transition-all\">\n                  <Smile className=\"w-4 h-4\" />\n                </button>\n              </DropdownMenuTrigger>\n              <DropdownMenuContent className=\"bg-[#1A1D21] border border-[#565856]/40 p-1 rounded-lg\">\n                <div className=\"flex gap-1\">\n                  {REACTION_EMOJIS.map(e => (\n                    <DropdownMenuItem key={e.id} onClick={() => reaction.toggle(e.id)} className=\"cursor-pointer hover:bg-[#222529] rounded p-1.5 justify-center\">\n                      <span className=\"text-lg leading-none\">{e.emoji}</span>\n                    </DropdownMenuItem>\n                  ))}\n                </div>\n              </DropdownMenuContent>\n            </DropdownMenu>\n          </div>\n          {replies.length > 0 && (\n            <button onClick={toggleReplies} className=\"flex items-center gap-2 mt-2 text-[13px] text-[#1D9BD1] font-medium hover:underline\">\n              <div className=\"flex -space-x-1\">\n                {replies.slice(0, 3).map(r => <img key={r.id} src={r.author.avatarUrl} alt=\"\" className=\"w-5 h-5 rounded border-2 border-[#1A1D21]\" />)}\n              </div>\n              {replies.length} {replies.length === 1 ? 'reply' : 'replies'}\n            </button>\n          )}\n          {showReplies && (\n            <div className=\"mt-3 border-l-2 border-[#565856]/30 pl-4 space-y-1\">\n              {replies.map(r => (\n                <SlackReply key={r.id} comment={r} tree={tree} currentUser={currentUser} />\n              ))}\n              <div className=\"flex gap-2 items-center\">\n                <input value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"Reply...\" className=\"flex-1 bg-[#222529] border border-[#565856]/50 rounded-lg px-3 py-2 text-sm text-white placeholder:text-[#ABABAD] outline-none focus:border-[#007A5A] transition-colors\" onKeyDown={e => { if (e.key === 'Enter') reply.submitReply(); }} />\n              </div>\n            </div>\n          )}\n        </div>\n      </div>\n      {/* Hover toolbar */}\n      <div className=\"opacity-0 group-hover:opacity-100 absolute right-3 -top-3 flex items-center bg-[#1A1D21] border border-[#565856]/40 rounded-lg shadow-xl overflow-hidden transition-all\">\n        <button onClick={() => reaction.toggle('like')} className=\"p-2 hover:bg-[#222529] text-[#ABABAD] text-xs transition-colors\">üëç</button>\n        <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"p-2 hover:bg-[#222529] text-[#ABABAD] transition-colors\"><MessageSquare className=\"w-3.5 h-3.5\" /></button>\n        {isAuthor && <button onClick={() => edit.startEditing(comment.content)} className=\"p-2 hover:bg-[#222529] text-[#ABABAD] text-xs transition-colors\">‚úèÔ∏è</button>}\n        {isAuthor && <button onClick={deleteComment} className=\"p-2 hover:bg-red-500/10 text-red-400 text-xs transition-colors\">üóëÔ∏è</button>}\n      </div>\n    </div>\n  );\n};\n\nconst SlackReply = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { reaction } = useComment(comment, {\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n\n  return (\n    <div className=\"flex gap-2 group/reply\">\n      <img src={comment.author.avatarUrl} alt=\"\" className=\"w-7 h-7 rounded-lg shrink-0\" />\n      <div className=\"flex-1\">\n        <div className=\"flex items-baseline gap-2\">\n          <span className=\"text-sm font-bold text-white\">{comment.author.name}</span>\n          <span className=\"text-[11px] text-[#ABABAD]\">{formatRelativeTime(comment.createdAt)}</span>\n        </div>\n        <p className=\"text-sm text-[#D1D2D3]\">{comment.content}</p>\n        {comment.reactions && comment.reactions.filter(r => r.count > 0).length > 0 && (\n          <div className=\"flex flex-wrap gap-1 mt-1\">\n            {comment.reactions.filter(r => r.count > 0).map(r => (\n              <button key={r.id} onClick={() => reaction.toggle(r.id)} className={`flex items-center gap-1 text-[11px] px-2 py-0.5 rounded-full border transition-colors ${r.isActive ? 'border-[#007A5A]/50 bg-[#007A5A]/15 text-[#1D9F73]' : 'border-[#565856]/50 text-[#D1D2D3] hover:bg-[#222529]'}`}>\n                {r.emoji} {r.count}\n              </button>\n            ))}\n          </div>\n        )}\n      </div>\n    </div>\n  );\n};\n\nconst SlackStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#1A1D21] text-[#D1D2D3] p-5 rounded-2xl\">\n      <div className=\"border-b border-[#565856]/30 pb-3 mb-3 flex items-center gap-2\">\n        <Hash className=\"w-4 h-4 text-[#ABABAD]\" />\n        <span className=\"text-sm font-bold text-white\">general</span>\n        <span className=\"text-xs text-[#ABABAD] ml-1\">|</span>\n        <span className=\"text-xs text-[#ABABAD]\">{tree.totalCount} messages</span>\n      </div>\n      <div className=\"space-y-0\">\n        {tree.comments.map(c => <SlackMessage key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n      </div>\n      <div className=\"mt-4 border border-[#565856]/40 rounded-xl overflow-hidden focus-within:border-[#565856] transition-colors\">\n        <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Message #general\" className=\"w-full bg-transparent px-4 py-3 text-sm text-white placeholder:text-[#ABABAD] outline-none\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n      </div>\n    </div>\n  );\n};\n\nexport default SlackStyle;\n";

export const StackOverflowStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { ChevronUp, ChevronDown } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst SOComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const dislikeReaction = comment.reactions?.find(r => r.id === 'dislike');\n  const netScore = (likeReaction?.count ?? 0) - (dislikeReaction?.count ?? 0);\n  const replies = comment.replies || [];\n\n  return (\n    <div className={`border-b border-[#3B4045]/60 py-5 last:border-0 ${depth > 0 ? 'ml-6 border-l border-[#3B4045]/30 pl-4' : ''}`}>\n      <div className=\"flex gap-5\">\n        <div className=\"flex flex-col items-center gap-1.5 shrink-0\">\n          <button onClick={() => reaction.toggle('like')} className={`p-1 rounded hover:bg-white/5 transition-colors ${likeReaction?.isActive ? 'text-[#F48024]' : 'text-[#6A737C]'}`}>\n            <ChevronUp className=\"w-7 h-7\" />\n          </button>\n          <span className={`text-xl font-medium ${netScore > 0 ? 'text-[#F48024]' : netScore < 0 ? 'text-[#cf4844]' : 'text-[#ACBCC3]'}`}>{netScore}</span>\n          <button onClick={() => reaction.toggle('dislike')} className={`p-1 rounded hover:bg-white/5 transition-colors ${dislikeReaction?.isActive ? 'text-[#cf4844]' : 'text-[#6A737C]'}`}>\n            <ChevronDown className=\"w-7 h-7\" />\n          </button>\n        </div>\n        <div className=\"flex-1\">\n          {edit.isEditing ? (\n            <div>\n              <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-[#1E1E1E] border border-[#3B4045] rounded-lg p-3 text-sm text-[#D1D5DB] font-mono min-h-[100px] focus:border-[#0A95FF] outline-none transition-colors\" />\n              <div className=\"flex gap-2 mt-3\">\n                <button onClick={edit.submitEdit} className=\"text-sm bg-[#0A95FF] text-white px-4 py-2 rounded-md font-medium hover:bg-[#0074CC] transition-colors\">Save edits</button>\n                <button onClick={edit.cancelEdit} className=\"text-sm text-[#6A737C] px-4 py-2 rounded-md border border-[#3B4045] hover:border-[#6A737C] transition-colors\">Cancel</button>\n              </div>\n            </div>\n          ) : (\n            <p className=\"text-[15px] text-[#D1D5DB] leading-relaxed\">{comment.content}</p>\n          )}\n          <div className=\"flex items-center justify-between mt-4\">\n            <div className=\"flex items-center gap-3 text-xs text-[#6A737C]\">\n              <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"hover:text-[#ACBCC3] transition-colors\">add a comment</button>\n              {isAuthor && <button onClick={() => edit.startEditing(comment.content)} className=\"hover:text-[#ACBCC3] transition-colors\">edit</button>}\n              {isAuthor && <button onClick={deleteComment} className=\"hover:text-red-400 transition-colors\">delete</button>}\n            </div>\n            <div className=\"flex items-center gap-2.5 bg-[#2D6085]/20 rounded-lg px-3 py-2\">\n              <span className=\"text-xs text-[#6A737C]\">answered {formatRelativeTime(comment.createdAt)}</span>\n              <img src={comment.author.avatarUrl} alt=\"\" className=\"w-5 h-5 rounded\" />\n              <span className=\"text-xs text-[#0A95FF] font-medium hover:underline cursor-pointer\">{comment.author.name}</span>\n            </div>\n          </div>\n          {reply.isReplying && (\n            <div className=\"mt-4 border-t border-[#3B4045]/40 pt-4\">\n              <textarea value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"Add a comment...\" className=\"w-full bg-[#1E1E1E] border border-[#3B4045] rounded-lg p-3 text-sm text-[#D1D5DB] min-h-[60px] focus:border-[#0A95FF] outline-none transition-colors\" autoFocus />\n              <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-sm bg-[#0A95FF] text-white px-4 py-2 rounded-md mt-2 disabled:opacity-40 font-medium hover:bg-[#0074CC] transition-colors\">Add Comment</button>\n            </div>\n          )}\n          {replies.length > 0 && (\n            <div className=\"mt-4 border-t border-[#3B4045]/40 pt-3\">\n              <button onClick={toggleReplies} className=\"text-xs text-[#6A737C] hover:text-[#ACBCC3] mb-2 transition-colors\">\n                {showReplies ? 'hide' : 'show'} {replies.length} {replies.length === 1 ? 'comment' : 'comments'}\n              </button>\n              {showReplies && replies.map(r => (\n                <SOComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />\n              ))}\n            </div>\n          )}\n        </div>\n      </div>\n    </div>\n  );\n};\n\nconst StackOverflowStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#1B1B1B] text-[#D1D5DB] p-5 rounded-2xl\" style={{ fontFamily: '-apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, sans-serif' }}>\n      <div className=\"flex items-center gap-3 mb-5 pb-3 border-b border-[#3B4045]/60\">\n        <h3 className=\"text-lg text-[#ACBCC3] font-medium\">{tree.totalCount} Answers</h3>\n        <div className=\"ml-auto text-xs text-[#6A737C]\">Sorted by: <span className=\"text-[#ACBCC3] font-medium\">Votes</span></div>\n      </div>\n      {tree.comments.map(c => <SOComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n      <div className=\"mt-6\">\n        <h3 className=\"text-base text-[#ACBCC3] mb-3 font-medium\">Your Answer</h3>\n        <textarea value={text} onChange={e => setText(e.target.value)} className=\"w-full bg-[#1E1E1E] border border-[#3B4045] rounded-lg p-3 text-sm text-[#D1D5DB] min-h-[120px] font-mono focus:border-[#0A95FF] outline-none transition-colors\" />\n        <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"mt-3 bg-[#0A95FF] text-white text-sm px-5 py-2.5 rounded-md hover:bg-[#0074CC] disabled:opacity-40 font-medium transition-colors\">Post Your Answer</button>\n      </div>\n    </div>\n  );\n};\n\nexport default StackOverflowStyle;\n";

export const TelegramStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst TGMessage = ({ comment, currentUser }: { comment: Comment; currentUser: CommentUser }) => {\n  const isOwn = comment.author.id === currentUser.id;\n\n  return (\n    <div className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-1.5`}>\n      {!isOwn && <img src={comment.author.avatarUrl} alt=\"\" className=\"w-8 h-8 rounded-full mr-2 mt-auto shrink-0 ring-1 ring-white/10\" />}\n      <div className={`max-w-[70%] rounded-2xl px-3.5 py-2 shadow-sm ${isOwn ? 'bg-[#2B5278] rounded-br-md' : 'bg-[#182533] rounded-bl-md'}`}>\n        {!isOwn && <p className=\"text-xs font-medium text-[#6AB3F3] mb-0.5\">{comment.author.name}</p>}\n        <p className=\"text-[14px] text-white leading-relaxed\">{comment.content}</p>\n        <p className={`text-[10px] text-right mt-1 ${isOwn ? 'text-[#6BB5E0]' : 'text-[#6E7F8D]'}`}>\n          {formatRelativeTime(comment.createdAt)}\n          {isOwn && ' ‚úì‚úì'}\n        </p>\n      </div>\n    </div>\n  );\n};\n\nconst TelegramStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#0E1621] rounded-2xl overflow-hidden\">\n      <div className=\"bg-[#17212B] px-4 py-3.5 flex items-center gap-3 border-b border-[#0E1621]\">\n        <div className=\"w-10 h-10 rounded-full bg-gradient-to-br from-[#2AABEE] to-[#229ED9] flex items-center justify-center text-white font-bold text-sm shadow-lg\">CT</div>\n        <div>\n          <p className=\"text-sm font-medium text-white\">Comment Thread</p>\n          <p className=\"text-[11px] text-[#6E7F8D]\">{tree.totalCount} messages</p>\n        </div>\n      </div>\n      <div className=\"p-4 space-y-0.5 min-h-[200px]\">\n        {tree.comments.map(c => <TGMessage key={c.id} comment={c} currentUser={currentUser} />)}\n      </div>\n      <div className=\"bg-[#17212B] px-3 py-2.5 flex items-center gap-2.5\">\n        <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Write a message...\" className=\"flex-1 bg-transparent text-sm text-white placeholder:text-[#6E7F8D] outline-none px-2 py-2\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n        <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} className=\"w-9 h-9 rounded-full flex items-center justify-center text-[#6AB3F3] hover:bg-white/5 transition-colors\">\n          <svg viewBox=\"0 0 24 24\" className=\"w-5 h-5 fill-current\"><path d=\"M1.101 21.757L23.8 12.028 1.101 2.3l.011 7.912 13.623 1.816-13.623 1.817-.011 7.912z\" /></svg>\n        </button>\n      </div>\n    </div>\n  );\n};\n\nexport default TelegramStyle;\n";

export const TikTokStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { Heart } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst TTComment = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { reply, reaction, showReplies, toggleReplies } = useComment(comment, {\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div className=\"mb-5\">\n      <div className=\"flex gap-3\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full shrink-0 ring-1 ring-white/10\" />\n        <div className=\"flex-1 min-w-0\">\n          <span className=\"text-[13px] font-bold text-white block\">{comment.author.name}</span>\n          <p className=\"text-[14px] text-[#E8E8E8] mt-0.5 leading-relaxed\">{comment.content}</p>\n          <div className=\"flex items-center gap-4 mt-2 text-xs text-[#8A8B91]\">\n            <span>{formatRelativeTime(comment.createdAt)}</span>\n            <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"font-medium hover:text-white transition-colors\">Reply</button>\n          </div>\n        </div>\n        <div className=\"flex flex-col items-center gap-1 shrink-0 mt-1\">\n          <button onClick={() => reaction.toggle('like')} className=\"p-1.5 active:scale-125 transition-transform\">\n            <Heart className={`w-5 h-5 transition-colors ${likeReaction?.isActive ? 'text-[#FE2C55] fill-[#FE2C55]' : 'text-[#8A8B91] hover:text-[#FE2C55]'}`} />\n          </button>\n          <span className=\"text-[11px] text-[#8A8B91]\">{likeReaction?.count || 0}</span>\n        </div>\n      </div>\n      {reply.isReplying && (\n        <div className=\"ml-13 mt-2.5 flex gap-2 items-center\">\n          <input value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder={`Reply to @${comment.author.name}`} className=\"flex-1 bg-[#2F2F2F] rounded-full px-4 py-2.5 text-sm text-white placeholder:text-[#8A8B91] outline-none focus:ring-1 focus:ring-[#FE2C55]/50 transition-all\" autoFocus onKeyDown={e => { if (e.key === 'Enter') reply.submitReply(); }} />\n          <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-sm font-bold text-[#FE2C55] disabled:opacity-40 transition-colors\">Post</button>\n        </div>\n      )}\n      {replies.length > 0 && (\n        <div className=\"ml-13 mt-3\">\n          <button onClick={toggleReplies} className=\"text-xs font-medium text-[#8A8B91] flex items-center gap-2 hover:text-white transition-colors\">\n            <div className=\"w-6 h-px bg-[#8A8B91]\" />\n            {showReplies ? 'Hide' : 'View'} {replies.length} replies\n          </button>\n          {showReplies && (\n            <div className=\"mt-3 space-y-3\">\n              {replies.map(r => (\n                <TTReply key={r.id} comment={r} tree={tree} currentUser={currentUser} />\n              ))}\n            </div>\n          )}\n        </div>\n      )}\n    </div>\n  );\n};\n\nconst TTReply = ({ comment, tree, currentUser }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser }) => {\n  const { reaction } = useComment(comment, {\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n\n  return (\n    <div className=\"flex gap-2.5\">\n      <img src={comment.author.avatarUrl} alt=\"\" className=\"w-7 h-7 rounded-full shrink-0\" />\n      <div className=\"flex-1\">\n        <span className=\"text-xs font-bold text-white\">{comment.author.name}</span>\n        <p className=\"text-[13px] text-[#E8E8E8]\">{comment.content}</p>\n        <span className=\"text-[11px] text-[#8A8B91]\">{formatRelativeTime(comment.createdAt)}</span>\n      </div>\n      <div className=\"flex flex-col items-center gap-0.5 shrink-0\">\n        <button onClick={() => reaction.toggle('like')} className=\"p-1 active:scale-125 transition-transform\">\n          <Heart className={`w-3.5 h-3.5 transition-colors ${likeReaction?.isActive ? 'text-[#FE2C55] fill-[#FE2C55]' : 'text-[#8A8B91] hover:text-[#FE2C55]'}`} />\n        </button>\n        {(likeReaction?.count ?? 0) > 0 && <span className=\"text-[10px] text-[#8A8B91]\">{likeReaction?.count}</span>}\n      </div>\n    </div>\n  );\n};\n\nconst TikTokStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#161823] text-white p-5 rounded-2xl\">\n      <div className=\"flex items-center justify-between mb-5\">\n        <h3 className=\"text-base font-bold\">Comments ({tree.totalCount})</h3>\n      </div>\n      {tree.comments.map(c => <TTComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n      <div className=\"flex gap-2.5 items-center mt-4 pt-4 border-t border-[#2F2F2F]\">\n        <img src={currentUser.avatarUrl} alt=\"\" className=\"w-8 h-8 rounded-full\" />\n        <div className=\"flex-1 bg-[#2F2F2F] rounded-full flex items-center px-4\">\n          <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Add comment...\" className=\"flex-1 bg-transparent text-sm text-white placeholder:text-[#8A8B91] outline-none py-2.5\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n          <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"text-sm font-bold text-[#FE2C55] disabled:opacity-40 transition-colors\">Post</button>\n        </div>\n      </div>\n    </div>\n  );\n};\n\nexport default TikTokStyle;\n";

export const TwitterStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { Heart, MessageCircle, Repeat2, BarChart3, Bookmark, Share } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst Tweet = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const replies = comment.replies || [];\n\n  return (\n    <div>\n      <div className=\"flex gap-3 px-4 py-3 border-b border-[#2F3336] hover:bg-white/[0.02] transition-colors\">\n        <div className=\"flex flex-col items-center shrink-0\">\n          <img src={comment.author.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full\" />\n          {replies.length > 0 && showReplies && <div className=\"w-0.5 flex-1 bg-[#333639] mt-1 rounded-full\" />}\n        </div>\n        <div className=\"flex-1 min-w-0\">\n          <div className=\"flex items-center gap-1\">\n            <span className=\"text-[15px] font-bold text-[#E7E9EA]\">{comment.author.name}</span>\n            {comment.author.isVerified && (\n              <svg viewBox=\"0 0 22 22\" className=\"w-4.5 h-4.5 text-[#1D9BF0]\" fill=\"currentColor\"><path d=\"M20.396 11c-.018-.646-.215-1.275-.57-1.816-.354-.54-.852-.972-1.438-1.246.223-.607.27-1.264.14-1.897-.131-.634-.437-1.218-.882-1.687-.47-.445-1.053-.75-1.687-.882-.633-.13-1.29-.083-1.897.14-.273-.587-.704-1.086-1.245-1.44S11.647 1.62 11 1.604c-.646.017-1.273.213-1.813.568s-.969.855-1.24 1.44c-.608-.223-1.267-.272-1.902-.14-.635.13-1.22.436-1.69.882-.445.47-.749 1.055-.878 1.688-.13.633-.08 1.29.144 1.896-.587.274-1.087.705-1.443 1.245-.356.54-.555 1.17-.574 1.817.02.647.218 1.276.574 1.817.356.54.856.972 1.443 1.245-.224.606-.274 1.263-.144 1.896.13.634.433 1.218.877 1.688.47.443 1.054.747 1.687.878.633.132 1.29.084 1.897-.136.274.586.706 1.084 1.246 1.439.54.354 1.17.551 1.816.569.647-.016 1.276-.213 1.817-.567s.972-.854 1.245-1.44c.604.239 1.266.296 1.903.164.636-.132 1.22-.447 1.68-.907.46-.46.776-1.044.908-1.681s.075-1.299-.165-1.903c.586-.274 1.084-.705 1.439-1.246.354-.54.551-1.17.569-1.816zM9.662 14.85l-3.429-3.428 1.293-1.302 2.072 2.072 4.4-4.794 1.347 1.246z\"/></svg>\n            )}\n            <span className=\"text-[15px] text-[#71767B]\">@{comment.author.name.toLowerCase().replace(' ', '')} ¬∑ {formatRelativeTime(comment.createdAt)}</span>\n          </div>\n          <p className=\"text-[15px] text-[#E7E9EA] leading-relaxed mt-1\">{comment.content}</p>\n          <div className=\"flex items-center justify-between mt-3 max-w-[400px] -ml-2\">\n            <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"flex items-center gap-1 text-[#71767B] hover:text-[#1D9BF0] group/btn transition-colors\">\n              <div className=\"p-2 rounded-full group-hover/btn:bg-[#1D9BF0]/10 transition-colors\"><MessageCircle className=\"w-[18px] h-[18px]\" /></div>\n              {replies.length > 0 && <span className=\"text-[13px]\">{replies.length}</span>}\n            </button>\n            <button className=\"flex items-center gap-1 text-[#71767B] hover:text-[#00BA7C] group/btn transition-colors\">\n              <div className=\"p-2 rounded-full group-hover/btn:bg-[#00BA7C]/10 transition-colors\"><Repeat2 className=\"w-[18px] h-[18px]\" /></div>\n            </button>\n            <button onClick={() => reaction.toggle('like')} className={`flex items-center gap-1 group/btn transition-colors ${likeReaction?.isActive ? 'text-[#F91880]' : 'text-[#71767B] hover:text-[#F91880]'}`}>\n              <div className=\"p-2 rounded-full group-hover/btn:bg-[#F91880]/10 transition-colors\"><Heart className={`w-[18px] h-[18px] ${likeReaction?.isActive ? 'fill-current' : ''}`} /></div>\n              {(likeReaction?.count ?? 0) > 0 && <span className=\"text-[13px]\">{likeReaction?.count}</span>}\n            </button>\n            <button className=\"text-[#71767B] hover:text-[#1D9BF0] group/btn transition-colors\">\n              <div className=\"p-2 rounded-full group-hover/btn:bg-[#1D9BF0]/10 transition-colors\"><BarChart3 className=\"w-[18px] h-[18px]\" /></div>\n            </button>\n            <div className=\"flex items-center gap-0\">\n              <button className=\"text-[#71767B] hover:text-[#1D9BF0] p-2 rounded-full hover:bg-[#1D9BF0]/10 transition-colors\"><Bookmark className=\"w-[18px] h-[18px]\" /></button>\n              <button className=\"text-[#71767B] hover:text-[#1D9BF0] p-2 rounded-full hover:bg-[#1D9BF0]/10 transition-colors\"><Share className=\"w-[18px] h-[18px]\" /></button>\n            </div>\n          </div>\n          {reply.isReplying && (\n            <div className=\"flex gap-3 mt-3 pt-3 border-t border-[#2F3336]\">\n              <img src={currentUser.avatarUrl} alt=\"\" className=\"w-8 h-8 rounded-full\" />\n              <div className=\"flex-1\">\n                <textarea value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"Post your reply\" className=\"w-full bg-transparent text-[15px] text-[#E7E9EA] placeholder:text-[#71767B] outline-none resize-none min-h-[50px]\" autoFocus />\n                <div className=\"flex justify-end\">\n                  <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"bg-[#1D9BF0] text-white text-sm font-bold px-5 py-2 rounded-full disabled:opacity-50 hover:bg-[#1A8CD8] transition-colors\">Reply</button>\n                </div>\n              </div>\n            </div>\n          )}\n        </div>\n        {isAuthor && <button onClick={deleteComment} className=\"text-lg text-[#71767B] hover:text-red-400 self-start transition-colors\">√ó</button>}\n      </div>\n      {replies.length > 0 && !showReplies && (\n        <button onClick={toggleReplies} className=\"text-sm text-[#1D9BF0] px-4 py-2.5 hover:bg-[#1D9BF0]/10 w-full text-left font-medium transition-colors\">Show replies</button>\n      )}\n      {showReplies && replies.map(r => <Tweet key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n    </div>\n  );\n};\n\nconst TwitterStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-black text-[#E7E9EA] rounded-2xl overflow-hidden\">\n      <div className=\"p-4 border-b border-[#2F3336]\">\n        <div className=\"flex gap-3\">\n          <img src={currentUser.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full\" />\n          <div className=\"flex-1\">\n            <textarea value={text} onChange={e => setText(e.target.value)} placeholder=\"What is happening?!\" className=\"w-full bg-transparent text-xl text-[#E7E9EA] placeholder:text-[#71767B] outline-none resize-none min-h-[50px]\" />\n            <div className=\"flex justify-end pt-3 border-t border-[#2F3336]\">\n              <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} disabled={!text.trim()} className=\"bg-[#1D9BF0] text-white text-[15px] font-bold px-6 py-2 rounded-full disabled:opacity-50 hover:bg-[#1A8CD8] transition-colors\">Post</button>\n            </div>\n          </div>\n        </div>\n      </div>\n      {tree.comments.map(c => <Tweet key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n    </div>\n  );\n};\n\nexport default TwitterStyle;\n";

export const WhatsAppStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { CheckCheck } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst WAMessage = ({ comment, tree, currentUser, isOwn = false }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; isOwn?: boolean }) => {\n  const { reaction } = useComment(comment, {\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n\n  return (\n    <div className={`flex ${isOwn ? 'justify-end' : 'justify-start'} mb-1.5`}>\n      <div className={`max-w-[75%] rounded-2xl px-3.5 py-2 relative shadow-sm ${isOwn ? 'bg-[#005C4B] rounded-tr-md' : 'bg-[#202C33] rounded-tl-md'}`}>\n        {!isOwn && <p className=\"text-xs font-medium text-[#06CF9C] mb-0.5\">{comment.author.name}</p>}\n        <p className=\"text-[14px] text-[#E9EDEF] leading-relaxed inline\">{comment.content}</p>\n        <span className=\"text-[10px] text-[#8696A0] ml-3 float-right mt-2 flex items-center gap-0.5\">\n          {formatRelativeTime(comment.createdAt)}\n          {isOwn && <CheckCheck className=\"w-4 h-4 text-[#53BDEB]\" />}\n        </span>\n        {(likeReaction?.count ?? 0) > 0 && (\n          <div className=\"absolute -bottom-3 right-2 bg-[#1F2C34] rounded-full px-2 py-0.5 flex items-center gap-1 text-xs shadow-md border border-white/5\">\n            ‚ù§Ô∏è {likeReaction?.count}\n          </div>\n        )}\n      </div>\n    </div>\n  );\n};\n\nconst WhatsAppStyle = ({ tree, currentUser }: Props) => {\n  const [text, setText] = useState('');\n\n  return (\n    <div className=\"bg-[#0B141A] rounded-2xl overflow-hidden\" style={{ backgroundImage: 'url(\"data:image/svg+xml,%3Csvg width=\\'60\\' height=\\'60\\' viewBox=\\'0 0 60 60\\' xmlns=\\'http://www.w3.org/2000/svg\\'%3E%3Cg fill=\\'none\\' fill-rule=\\'evenodd\\'%3E%3Cg fill=\\'%23111B21\\' fill-opacity=\\'0.4\\'%3E%3Cpath d=\\'M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z\\'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E\")' }}>\n      <div className=\"bg-[#202C33] px-4 py-3 flex items-center gap-3\">\n        <div className=\"w-10 h-10 rounded-full bg-gradient-to-br from-[#25D366] to-[#128C7E] flex items-center justify-center text-white font-bold text-sm\">CT</div>\n        <div>\n          <p className=\"text-sm font-medium text-[#E9EDEF]\">Comment Thread</p>\n          <p className=\"text-[11px] text-[#8696A0]\">{tree.totalCount} messages</p>\n        </div>\n      </div>\n      <div className=\"p-4 space-y-1 min-h-[200px]\">\n        {tree.comments.map(c => (\n          <WAMessage key={c.id} comment={c} tree={tree} currentUser={currentUser} isOwn={c.author.id === currentUser.id} />\n        ))}\n      </div>\n      <div className=\"bg-[#202C33] px-3 py-2.5 flex items-center gap-2.5\">\n        <div className=\"flex-1 bg-[#2A3942] rounded-full px-4 py-2.5\">\n          <input value={text} onChange={e => setText(e.target.value)} placeholder=\"Type a message\" className=\"w-full bg-transparent text-sm text-[#E9EDEF] placeholder:text-[#8696A0] outline-none\" onKeyDown={e => { if (e.key === 'Enter' && text.trim()) { tree.addComment(text.trim()); setText(''); } }} />\n        </div>\n        <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); } }} className=\"w-10 h-10 rounded-full bg-[#00A884] flex items-center justify-center text-white shrink-0 hover:bg-[#06CF9C] transition-colors shadow-lg\">\n          <svg viewBox=\"0 0 24 24\" className=\"w-5 h-5 fill-current\"><path d=\"M1.101 21.757L23.8 12.028 1.101 2.3l.011 7.912 13.623 1.816-13.623 1.817-.011 7.912z\" /></svg>\n        </button>\n      </div>\n    </div>\n  );\n};\n\nexport default WhatsAppStyle;\n";

export const YouTubeStyle_CODE = "import type { CommentUser, UseCommentTreeReturn, Comment } from '@hasthiya_/headless-comments-react';\nimport { formatRelativeTime } from '@hasthiya_/headless-comments-react';\nimport { useComment } from '@hasthiya_/headless-comments-react/headless';\nimport { useSortedComments } from '@hasthiya_/headless-comments-react/headless';\nimport { ThumbsUp, ThumbsDown, ChevronDown, ChevronUp } from 'lucide-react';\nimport { useState } from 'react';\n\ninterface Props { tree: UseCommentTreeReturn; currentUser: CommentUser; }\n\nconst YTComment = ({ comment, tree, currentUser, depth = 0 }: { comment: Comment; tree: UseCommentTreeReturn; currentUser: CommentUser; depth?: number }) => {\n  const { isAuthor, edit, reply, reaction, showReplies, toggleReplies, deleteComment } = useComment(comment, {\n    onEdit: async (id, c) => { tree.editComment(id, c); },\n    onReply: async (id, c) => { tree.addReply(id, c); },\n    onReaction: async (id, rid) => { tree.toggleReaction(id, rid); },\n    onDelete: async (id) => { tree.deleteComment(id); },\n  });\n  const likeReaction = comment.reactions?.find(r => r.id === 'like');\n  const dislikeReaction = comment.reactions?.find(r => r.id === 'dislike');\n  const replies = comment.replies || [];\n\n  return (\n    <div className={`${depth > 0 ? 'ml-14' : ''} mb-5`}>\n      <div className=\"flex gap-3\">\n        <img src={comment.author.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full shrink-0\" />\n        <div className=\"flex-1\">\n          <div className=\"flex items-center gap-2 mb-0.5\">\n            <span className=\"text-[13px] font-medium text-white\">{comment.author.name}</span>\n            <span className=\"text-xs text-[#AAAAAA]\">{formatRelativeTime(comment.createdAt)}</span>\n          </div>\n          {edit.isEditing ? (\n            <div>\n              <textarea value={edit.editContent} onChange={e => edit.setEditContent(e.target.value)} className=\"w-full bg-transparent border-b-2 border-white/30 text-sm text-white py-1.5 outline-none focus:border-[#3EA6FF] transition-colors\" />\n              <div className=\"flex justify-end gap-2 mt-3\">\n                <button onClick={edit.cancelEdit} className=\"text-sm text-[#AAAAAA] px-4 py-2 rounded-full hover:bg-white/10 transition-colors\">Cancel</button>\n                <button onClick={edit.submitEdit} className=\"text-sm bg-[#3EA6FF] text-black font-medium px-4 py-2 rounded-full hover:bg-[#65B8FF] transition-colors\">Save</button>\n              </div>\n            </div>\n          ) : (\n            <p className=\"text-[14px] text-[#F1F1F1] leading-relaxed\">{comment.content}</p>\n          )}\n          {!edit.isEditing && (\n            <div className=\"flex items-center gap-1 mt-2\">\n              <button onClick={() => reaction.toggle('like')} className={`flex items-center gap-1.5 p-2 rounded-full hover:bg-white/10 transition-colors ${likeReaction?.isActive ? 'text-[#3EA6FF]' : 'text-[#AAAAAA]'}`}>\n                <ThumbsUp className=\"w-4 h-4\" />\n                {(likeReaction?.count ?? 0) > 0 && <span className=\"text-xs\">{likeReaction?.count}</span>}\n              </button>\n              <button onClick={() => reaction.toggle('dislike')} className={`p-2 rounded-full hover:bg-white/10 transition-colors ${dislikeReaction?.isActive ? 'text-white' : 'text-[#AAAAAA]'}`}><ThumbsDown className=\"w-4 h-4\" /></button>\n              <button onClick={reply.isReplying ? reply.cancelReply : reply.openReply} className=\"text-xs font-medium text-[#AAAAAA] hover:bg-white/10 px-3 py-2 rounded-full transition-colors\">Reply</button>\n              {isAuthor && <button onClick={() => edit.startEditing(comment.content)} className=\"text-xs text-[#AAAAAA] hover:bg-white/10 px-3 py-2 rounded-full transition-colors\">Edit</button>}\n              {isAuthor && <button onClick={deleteComment} className=\"text-xs text-red-400/80 hover:bg-red-400/10 px-3 py-2 rounded-full transition-colors\">Delete</button>}\n            </div>\n          )}\n          {reply.isReplying && (\n            <div className=\"flex gap-3 mt-3\">\n              <img src={currentUser.avatarUrl} alt=\"\" className=\"w-7 h-7 rounded-full mt-1\" />\n              <div className=\"flex-1\">\n                <input value={reply.replyContent} onChange={e => reply.setReplyContent(e.target.value)} placeholder=\"Add a reply...\" className=\"w-full bg-transparent border-b-2 border-white/20 text-sm text-white py-1.5 outline-none placeholder:text-[#AAAAAA] focus:border-white transition-colors\" autoFocus onKeyDown={e => { if (e.key === 'Enter') reply.submitReply(); }} />\n                <div className=\"flex justify-end gap-2 mt-3\">\n                  <button onClick={reply.cancelReply} className=\"text-sm text-[#AAAAAA] px-4 py-2 rounded-full hover:bg-white/10 transition-colors\">Cancel</button>\n                  <button onClick={reply.submitReply} disabled={!reply.replyContent.trim()} className=\"text-sm bg-[#3EA6FF] text-black font-medium px-4 py-2 rounded-full disabled:opacity-40 disabled:bg-white/10 disabled:text-[#AAAAAA] hover:bg-[#65B8FF] transition-colors\">Reply</button>\n                </div>\n              </div>\n            </div>\n          )}\n          {replies.length > 0 && (\n            <button onClick={toggleReplies} className=\"flex items-center gap-1.5 text-sm font-medium text-[#3EA6FF] mt-2 hover:bg-[#3EA6FF]/10 px-3 py-1.5 rounded-full -ml-3 transition-colors\">\n              {showReplies ? <ChevronUp className=\"w-4 h-4\" /> : <ChevronDown className=\"w-4 h-4\" />}\n              {replies.length} replies\n            </button>\n          )}\n          {showReplies && replies.map(r => <YTComment key={r.id} comment={r} tree={tree} currentUser={currentUser} depth={depth + 1} />)}\n        </div>\n      </div>\n    </div>\n  );\n};\n\nconst YouTubeStyle = ({ tree, currentUser }: Props) => {\n  const { sortedComments, sortOrder, setSortOrder } = useSortedComments(tree.comments, 'popular');\n  const [text, setText] = useState('');\n  const [focused, setFocused] = useState(false);\n\n  return (\n    <div className=\"bg-[#0F0F0F] text-white p-5 rounded-2xl\">\n      <div className=\"flex items-center gap-6 mb-5\">\n        <h3 className=\"text-base font-bold\">{tree.totalCount} Comments</h3>\n        <button onClick={() => setSortOrder(sortOrder === 'popular' ? 'newest' : 'popular')} className=\"flex items-center gap-1.5 text-sm text-[#AAAAAA] hover:text-white transition-colors\">\n          <svg viewBox=\"0 0 24 24\" className=\"w-5 h-5 fill-current\"><path d=\"M3 18h6v-2H3v2zM3 6v2h18V6H3zm0 7h12v-2H3v2z\" /></svg>\n          Sort by\n        </button>\n      </div>\n      <div className=\"flex gap-3 mb-6\">\n        <img src={currentUser.avatarUrl} alt=\"\" className=\"w-10 h-10 rounded-full\" />\n        <div className=\"flex-1\">\n          <input value={text} onChange={e => setText(e.target.value)} onFocus={() => setFocused(true)} placeholder=\"Add a comment...\" className=\"w-full bg-transparent border-b-2 border-white/10 text-sm text-white py-2 outline-none placeholder:text-[#AAAAAA] focus:border-white transition-colors\" />\n          {focused && (\n            <div className=\"flex justify-end gap-2 mt-3\">\n              <button onClick={() => { setFocused(false); setText(''); }} className=\"text-sm text-[#AAAAAA] px-4 py-2 rounded-full hover:bg-white/10 transition-colors\">Cancel</button>\n              <button onClick={() => { if (text.trim()) { tree.addComment(text.trim()); setText(''); setFocused(false); } }} disabled={!text.trim()} className=\"text-sm bg-[#3EA6FF] text-black font-medium px-4 py-2 rounded-full disabled:opacity-40 disabled:bg-white/10 disabled:text-[#AAAAAA] hover:bg-[#65B8FF] transition-colors\">Comment</button>\n            </div>\n          )}\n        </div>\n      </div>\n      {sortedComments.map(c => <YTComment key={c.id} comment={c} tree={tree} currentUser={currentUser} />)}\n    </div>\n  );\n};\n\nexport default YouTubeStyle;\n";

